-- Отзывы
INSERT OR IGNORE INTO reviews (client_name, client_company, client_position, rating, review_text, is_approved, is_active) VALUES
('Александр Петров', 'ООО "ЛогистикПро"', 'Директор по логистике', 5, 'Обратились в компанию для заказа мобильной погрузочной рампы. Консультанты помогли подобрать оптимальный вариант под наши задачи. Особенно порадовало соотношение цены и качества. Обязательно будем обращаться ещё!', 1, 1),
('Михаил Сидоров', 'АО "ТрансСервис"', 'Главный инженер', 5, 'Заказывали гидравлическую рампу на 10 тонн. Качество изготовления отличное, сварка аккуратная. Доставили вовремя. Рекомендую данного производителя.', 1, 1),
('Елена Козлова', 'ИП Козлова Е.В.', 'Владелец склада', 5, 'Приобрели рампу Т-9/7 для нашего небольшого склада. Очень довольны покупкой — значительно ускорила процесс погрузки-разгрузки. Спасибо за качественный товар!', 1, 1);

-- FAQ
INSERT OR IGNORE INTO faq (question, answer, category, sort_order) VALUES
('Чем отличается рампа от эстакады?', 'Погрузочная рампа — это мобильная конструкция с возможностью регулировки высоты, которую можно перемещать по территории. Эстакада — стационарная конструкция, устанавливаемая в определенном месте. Рампы более универсальны и подходят для складов с разными типами транспорта.', 'Общие вопросы', 1),
('Какая высота рампы подходит для разгрузки?', 'Для обычных грузовиков рекомендуется высота рампы 1200 мм. Для низкорамных грузовиков — 1000 мм. Для вагонов — 1500 мм. Наши рампы имеют регулируемую высоту подъема, что позволяет работать с различными типами транспорта.', 'Технические вопросы', 2),
('Какая гарантия на продукцию?', 'Гарантия на все наши изделия составляет 1 год при соблюдении правил эксплуатации. Также мы осуществляем послегарантийное обслуживание и поставку запасных частей.', 'Гарантия и сервис', 3),
('Осуществляете ли доставку по России?', 'Да, мы осуществляем доставку по всей России. Стоимость доставки рассчитывается индивидуально в зависимости от региона. Выгодная доставка по Владимирской, Ярославской, Нижегородской и другим областям ЦФО и ПФО.', 'Доставка', 4),
('Можно ли заказать рампу по индивидуальным размерам?', 'Да, мы изготавливаем рампы и эстакады по индивидуальным размерам и требованиям заказчика. Свяжитесь с нами для консультации и расчета стоимости.', 'Заказ', 5);

-- Страницы
INSERT OR IGNORE INTO pages (slug, title, content, seo_title, seo_description, template) VALUES
('o-kompanii', 'О компании', '<h2>О компании Armata-Rampa</h2><p>Мы являемся одной из ведущих российских компаний с лицензией на проектирование и изготовление погрузочных рамп всех типов.</p><h3>Наши преимущества:</h3><ul><li><strong>Собственное производство</strong> — контролируем качество на всех этапах</li><li><strong>Сертифицированная продукция</strong> — вся продукция соответствует ГОСТ</li><li><strong>Гарантия 1 год</strong> — при правильной эксплуатации</li><li><strong>Доставка по России</strong> — выгодные условия для всех регионов</li></ul>', 'О компании Armata-Rampa - производитель погрузочных рамп и эстакад', 'Armata-Rampa — производитель погрузочных рамп и эстакад. Собственное производство, гарантия качества, доставка по России.', 'about'),
('dostavka', 'Доставка и оплата', '<h2>Доставка и оплата</h2><p>Осуществляем доставку по всей России. Особенно выгодные условия для регионов:</p><ul><li>Владимирская область</li><li>Ярославская область</li><li>Нижегородская область</li><li>Республика Татарстан</li><li>Республика Башкортостан</li><li>Пермский край</li></ul><h3>Способы оплаты:</h3><ul><li>Безналичный расчет (для юр. лиц)</li><li>Оплата по счету</li></ul><p>Стоимость указана с НДС 20%.</p>', 'Доставка и оплата | Armata-Rampa', 'Условия доставки погрузочных рамп и эстакад по России. Выгодные условия для регионов. Оплата с НДС.', 'delivery'),
('kontakty', 'Контакты', '<h2>Контакты</h2><p>Свяжитесь с нами любым удобным способом:</p>', 'Контакты | Armata-Rampa - производитель рамп и эстакад', 'Контакты компании Armata-Rampa. Телефон, email, адрес производства. Консультация и расчет стоимости.', 'contacts');

